$(document).ready(function(){
    var $navFixed = $('.navigation-fixed');
    var $dropDown = $('.drop-down');
    var $menuDropDown = $('.drop-down-list');
    var $iconDropDown = $('.drop-down-icon');
    var $curentDropDown = $('.drop-down-curent');
    var $itemDropDown = $('.drop-down-item');
    
    $(window).on("scroll" , function(){
        if ( $(window).scrollTop() > 180 ){
        $navFixed.fadeIn(500);
        }else{
            $navFixed.fadeOut(500); 
        }
    })
    $dropDown.click(function(){
        $menuDropDown.toggleClass('active-menu');
        $dropDown.toggleClass('active-link');
        $iconDropDown.toggleClass('up');
        $iconDropDown.toggleClass('down');
    })
    
    $('.drop-down-item').on('click', function(e){
        var $c = $(e.target).text();
        $('.text').text($c);
        
        
      });
      
    

 });